ON CONFLICT (name_normalized, plz) WHERE is_active = true
DO UPDATE SET
  website = COALESCE(EXCLUDED.website, hospitals.website),
  beds_count = COALESCE(EXCLUDED.beds_count, hospitals.beds_count),
  type = COALESCE(EXCLUDED.type, hospitals.type),
  source = array_cat(hospitals.source, EXCLUDED.source),
  verified = true;

COMMENT ON TABLE hospitals IS 'Scaled to 500+ hospitals - comprehensive coverage of German healthcare system';
